import "core-js/modules/es.array.push.js";
import axios from 'axios';
import * as echarts from 'echarts';
// 导入生命周期函数
import { onMounted } from 'vue';
export default {
  name: 'ChinaMap',
  setup() {
    const getdata = () => {
      // 录取数据
      var Cdata = [];
      //地图数据
      var mapdata;
      axios.get('http://localhost:8888/chinamap/list').then(snum => {
        // 处理录取数据
        const data = snum.data;
        var area = []; //地区
        var sumb = []; //人数
        // 用循环遍历数据
        for (var item = 0; item < data.length; item++) {
          area.push(data[item].name);
          sumb.push(data[item].value);
        }
        // 加入录取数据
        for (var i = 0; i < area.length; i++) {
          Cdata.push({
            value: sumb[i],
            name: area[i]
          });
        }
        // then请求成功，catch请求失败
        axios.get('http://localhost:8080/china.json').then(res => {
          // 储存地图数据
          mapdata = res.data;
          var chinaCharts = echarts.init(document.getElementById('CN_Map'));
          // 在axios的回调函数中注册地图矢量数据
          echarts.registerMap('chinaMap', mapdata);
          var CNoption = {
            title: {
              left: 'center',
              text: '2022年各省录取人数'
            },
            // 鼠标悬停显示数据
            tooltip: {
              trigger: 'item',
              formatter: '{b}<br/>人数：{c}'
            },
            // 热力图
            visualMap: {
              show: true,
              min: 0,
              max: 4100,
              text: ['High', 'Low'],
              realtime: false,
              calculable: true,
              inRange: {
                color: ['#98bbff', '#4f7bde']
              }
            },
            series: [{
              type: 'map',
              map: 'chinaMap',
              data: Cdata,
              roam: true,
              //允许缩放以及拖动效果
              label: {
                show: true //显示标签
              },

              zoom: 1.5,
              //初始的大小比例
              // center: [],设置地图中心店的坐标
              // 鼠标悬停效果
              emphasis: {
                label: {
                  textStyle: {
                    color: 'pink'
                  } //悬停时字体颜色
                },

                itemStyle: {
                  areaColor: 'red' //区域颜色
                }
              },

              itemStyle: {
                // 设置边框为白色
                normal: {
                  borderWidth: 0.5,
                  //边界线大小
                  borderColor: '#fff' //边界线颜色
                }
              }
            }]
          };

          chinaCharts.setOption(CNoption);
        }).catch(error => {
          console.log(error);
        });
      }).catch(error => {
        console.log(error);
      });
    };
    // 调用，执行函数
    onMounted(() => {
      getdata();
    });
  }
};
