import axios from 'axios';
import * as echarts from 'echarts';
// 导入生命周期函数
import { onMounted } from 'vue';
export default {
  name: 'ChinaMap',
  setup() {
    const getdata = () => {
      // then请求成功，catch请求失败
      axios.get('http://localhost:8080/china.json').then(res => {
        // 输出矢量地图数据
        console.log(res.data);
        axios.get();
        var chinaCharts = echarts.init(document.getElementById('CN_Map'));
        // 在axios的回调函数中注册地图矢量数据
        echarts.registerMap('chinaMap', res.data);
        var CNoption = {
          title: {
            left: 'center',
            text: '2022年各省录取人数'
          },
          // visualMap: {
          //   min: 0,
          //   max: 4100,
          //   text: ['High', 'Low'],
          //   realtime: false,
          //   calculable: true,
          //   inRange: {
          //     color: ['lightskyblue', 'yellow', 'orangered']
          //   }
          // },
          geo: {
            type: 'map',
            map: 'chinaMap',
            roam: true,
            //允许缩放以及拖动效果
            label: {
              show: true //显示标签
            },

            zoom: 1.5,
            //初始的大小比例
            // center: [],设置地图中心店的坐标

            //鼠标悬停效果
            emphasis: {
              label: {
                textStyle: {
                  color: 'pink'
                } //悬停时字体颜色
              },

              itemStyle: {
                areaColor: 'red' //区域颜色
              }
            },

            itemStyle: {
              // 设置边框为白色
              normal: {
                borderWidth: 0.5,
                //边界线大小
                borderColor: '#fff' //边界线颜色
              }
            }
          }
          // series: [
          //   {
          //     title:{
          //       left: 'center',
          //       text: '2022年各省录取人数'
          //     } 
          //   }
          // ]
        };

        chinaCharts.setOption(CNoption);
      }).catch(error => {
        console.log(error);
      });
    };
    // 调用，执行函数
    onMounted(() => {
      getdata();
    });
  }
};
