import "core-js/modules/es.array.push.js";
import axios from 'axios';
import * as echarts from 'echarts';
// 导入生命周期函数
import { onMounted } from 'vue';
export default {
  name: 'ChinaMap',
  setup() {
    const getdata = () => {
      // 录取数据
      var Cdata = [];
      var mapdata;
      axios.get('http://localhost:8888/chinamap/list').then(snum => {
        // 输出录取人数数据
        console.log(snum.data);
        // 处理数据
        const data = snum.data;
        var area = [];
        var sumb = [];
        // 用循环遍历数据
        for (var item = 0; item < data.length; item++) {
          area.push(data[item].name);
          sumb.push(data[item].value);
        }
        // 录取数据
        for (var i = 0; i < area.length; i++) {
          Cdata.push({
            value: sumb[i],
            names: area[i]
          });
        }
        // then请求成功，catch请求失败
        axios.get('http://localhost:8080/china.json').then(res => {
          // 输出矢量地图数据
          console.log(res.data);
          // 储存地图数据
          mapdata = res.data;
          var chinaCharts = echarts.init(document.getElementById('CN_Map'));

          // 在axios的回调函数中注册地图矢量数据
          echarts.registerMap('chinaMap', mapdata);
          var CNoption = {
            title: {
              left: 'center',
              text: '2022年各省录取人数'
            },
            tooltip: {
              trigger: 'item',
              formatter: '{b}<br/>{c} (p / km2)'
            },
            visualMap: {
              show: true,
              min: 0,
              max: 4100,
              text: ['High', 'Low'],
              realtime: false,
              calculable: true,
              inRange: {
                color: ['lightskyblue', 'yellow', 'orangered']
              }
            },
            geo: {
              type: 'map',
              map: 'chinaMap',
              data: Cdata,
              roam: true //允许缩放以及拖动效果
              // label: {
              //   show: true  //显示标签
              // },

              // zoom: 1.5,    //初始的大小比例
              // center: [],设置地图中心店的坐标
              //鼠标悬停效果
              // emphasis: {
              //   label: {
              //     textStyle: { color: 'pink' },//悬停时字体颜色
              //   },
              //   itemStyle: {
              //     areaColor: 'red' //区域颜色
              //   }
              // },
              // itemStyle: {
              //   // 设置边框为白色
              //   normal: {
              //     borderWidth: 0.5,//边界线大小
              //     borderColor: '#fff',//边界线颜色
              //   }
              // }
            }
          };

          chinaCharts.setOption(CNoption);
        }).catch(error => {
          console.log(error);
        });
      }).catch(error => {
        console.log(error);
      });
    };
    // 调用，执行函数
    onMounted(() => {
      getdata();
    });
  }
};
